CREATE TABLE clientlist.clients ( id INT UNSIGNED NOT NULL AUTO_INCREMENT , name VARCHAR(255) NOT NULL , phone VARCHAR(255) NOT NULL , email VARCHAR(255) NOT NULL , address VARCHAR(255) NOT NULL , cp VARCHAR(255) NOT NULL , city VARCHAR(255) NOT NULL , customer VARCHAR(255) NOT NULL , PRIMARY KEY (id)) ENGINE = InnoDB CHARSET=utf8 COLLATE utf8_general_ci;